import { H2, H3, StackLayout, Text, useId } from "@salt-ds/core";
import {
  Carousel,
  CarouselControls,
  CarouselSlide,
  CarouselSlider,
} from "@salt-ds/lab";
import clsx from "clsx";
import type { ReactElement } from "react";
import { sliderData } from "./exampleData";
import styles from "./index.module.css";

function renderSlideContent(index: number) {
  return (
    <StackLayout gap={1}>
      <H3 id={`slide-title-${index}`}>
        Categorical bold background {index + 1}
      </H3>
      <Text>
        Categorical colors are used for data visualization and other components
        that support categories.
      </Text>
    </StackLayout>
  );
}

const renderMediaPlaceholder = (index: number) => (
  <div
    className={clsx(
      styles.carouselImagePlaceholder,
      styles[`carouselImagePlaceholder-${index + 1}`],
    )}
  />
);

export const Appearance = (): ReactElement => {
  return (
    <StackLayout direction="row" gap={6}>
      <Carousel aria-label="Account overview bordered">
        <CarouselControls title={<H2>Bordered</H2>} />
        <CarouselSlider>
          {sliderData.map((slide, index) => {
            const carousel1SlideId = useId();
            return (
              <CarouselSlide
                appearance="bordered"
                key={carousel1SlideId}
                aria-labelledby={`slide-title-${carousel1SlideId}`}
                media={
                  <img
                    alt={`stock content to show in carousel slide ${index}`}
                    className={clsx(styles.carouselImagePlaceholder)}
                    src={slide.image}
                  />
                }
                header={
                  <H3 id={`slide-title-${carousel1SlideId}`}>{slide.title}</H3>
                }
              >
                <Text>{slide.content}</Text>
              </CarouselSlide>
            );
          })}
        </CarouselSlider>
      </Carousel>
      <Carousel aria-label="Account overview">
        <CarouselControls title={<H2>Transparent</H2>} />
        <CarouselSlider>
          {sliderData.map((slide, index) => {
            const carousel2SlideId = useId();
            return (
              <CarouselSlide
                key={carousel2SlideId}
                aria-labelledby={`slide-title-${carousel2SlideId}`}
                media={
                  <img
                    alt={`stock content to show in carousel slide ${index}`}
                    className={clsx(styles.carouselImagePlaceholder)}
                    src={slide.image}
                  />
                }
                header={
                  <H3 id={`slide-title-${carousel2SlideId}`}>{slide.title}</H3>
                }
              >
                <Text>{slide.content}</Text>
              </CarouselSlide>
            );
          })}
        </CarouselSlider>
      </Carousel>
    </StackLayout>
  );
};

import { Button, H3, StackLayout, Text, useId } from "@salt-ds/core";
import { Carousel, CarouselSlide, CarouselSlider } from "@salt-ds/lab";
import clsx from "clsx";
import { type ReactElement, useState } from "react";
import { sliderData } from "./exampleData";
import styles from "./index.module.css";

export const ControlledCarousel = (): ReactElement => {
  const [slide, setSlide] = useState<number>(0);
  return (
    <StackLayout>
      <StackLayout gap={1} direction="row" align="center">
        <Button onClick={() => setSlide(slide - 1)} disabled={slide === 0}>
          Left
        </Button>
        <Button
          onClick={() => setSlide(slide + 1)}
          disabled={slide >= sliderData.length - 1}
        >
          Right
        </Button>
        <Text>Current slide: {slide + 1}</Text>
      </StackLayout>
      <Carousel aria-label="Account overview" activeSlideIndex={slide}>
        <CarouselSlider onSelectionChange={(_, index) => setSlide(index)}>
          {sliderData.map((slide, index) => {
            const slideId = useId();
            return (
              <CarouselSlide
                key={slideId}
                aria-labelledby={`slide-title-${slideId}`}
                media={
                  <img
                    alt={`stock content to show in carousel slide ${index}`}
                    className={clsx(styles.carouselImagePlaceholder)}
                    src={slide.image}
                  />
                }
                header={<H3 id={`slide-title-${slideId}`}>{slide.title}</H3>}
              >
                <Text>{slide.content}</Text>
              </CarouselSlide>
            );
          })}
        </CarouselSlider>
      </Carousel>
    </StackLayout>
  );
};

