import { AddDocumentIcon } from "@salt-ds/icons";
import type { ReactElement } from "react";

export const BasicIcon = (): ReactElement => <AddDocumentIcon size={2} />;

import "@salt-ds/icons/saltIcons.css";

const name = "AddDocument";

export const CssBackgroundImage = () => <div className={`saltIcons-${name}`} />;

import { StackLayout } from "@salt-ds/core";
import { ArrowDownIcon, ArrowUpIcon } from "@salt-ds/icons";
import type { ReactElement } from "react";

export const CustomColors = (): ReactElement => {
  return (
    <StackLayout direction="row">
      <div style={{ color: "var(--salt-status-positive-foreground)" }}>
        <ArrowUpIcon color="inherit" size={2} />
      </div>
      <ArrowDownIcon
        style={{ fill: "var(--salt-status-negative-foreground)" }}
        size={2}
      />
    </StackLayout>
  );
};

