import { Pill } from "@salt-ds/core";
import { CloseIcon } from "@salt-ds/icons";
import type { ReactElement } from "react";

const handleClose = () => {
  console.log("closed");
};

export const Closable = (): ReactElement => (
  <Pill onClick={handleClose}>
    Closable Pill <CloseIcon style={{ marginLeft: "auto" }} />
  </Pill>
);

import { Pill } from "@salt-ds/core";
import type { ReactElement } from "react";

const handleClick = () => {
  console.log("clicked");
};

export const Default = (): ReactElement => (
  <Pill onClick={handleClick}>Clickable Pill</Pill>
);

import { Pill } from "@salt-ds/core";
import type { ReactElement } from "react";

export const Disabled = (): ReactElement => <Pill disabled>Disabled Pill</Pill>;

import { Pill } from "@salt-ds/core";
import { FavoriteIcon } from "@salt-ds/icons";
import type { ReactElement } from "react";

const handleClick = () => {
  console.log("clicked");
};

export const Icon = (): ReactElement => (
  <Pill onClick={handleClick}>
    <FavoriteIcon /> Pill with Icon
  </Pill>
);
